prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
prefix mosp: <http://dfki.de/examples/stig_mosp#>
prefix ord: <http://dfki.de/examples/stig_mosp/orders#>
prefix st: <http://dfki.de/stigld/>
prefix schema: <http://schema.org/>

SELECT * WHERE {
    SELECT DISTINCT (count(?order) as ?count ) WHERE {
        ?order a schema:Order        
        FILTER(?c = 1)
        {SELECT(count(?product) as ?c) ?order WHERE {
            ?order schema:orderedItem ?product .
        } GROUP BY ?order }
    }
}

prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
prefix mosp: <http://dfki.de/examples/stig_mosp#>
prefix ord: <http://dfki.de/examples/stig_mosp/orders#>
prefix st: <http://dfki.de/stigld/>
prefix schema: <http://schema.org/>

## If located at a Topos, agent will move to a neighbour order with highest concentration
## of pheromones

DELETE {
  mosp:Agent st:locatedAt ?from
}
INSERT {
  mosp:Agent st:locatedAt ?to
}
WHERE {
mosp:Agent st:locatedAt ?from .
{SELECT DISTINCT  ?to WHERE {

  {SELECT DISTINCT ?to ?count WHERE {
    mosp:Agent st:locatedAt ?from .
    ?from st:adjacentTo ?to .
    {SELECT DISTINCT ?to (COUNT(?marker) as ?count) WHERE {
        ?to st:carries ?marker . ?marker a st:Trace  .
    } GROUP BY ?to }}}

  {SELECT (max(?count) as ?max) WHERE {
   {SELECT DISTINCT ?to (COUNT(?marker) as ?count) WHERE {
        mosp:Agent st:locatedAt ?from .
        ?from st:adjacentTo ?to .
        ?to st:carries ?marker . ?marker a st:Trace  .
    } GROUP BY ?to }}}

  FILTER(?count = ?max)
    } ORDER BY RAND() LIMIT 1}} ;


## Send agent to random order if not yet present anywhere

INSERT {
   mosp:Agent st:locatedAt ?randomOrder
}
WHERE {
   {SELECT ?randomOrder WHERE  {
    {SELECT (count(?item) as ?total) ?randomOrder WHERE {
      ?randomOrder a schema:Order ; schema:orderedItem ?item .
    } GROUP BY ?randomOrder }

    {SELECT (min(?total) as ?min) WHERE {
    {SELECT (count(?item) as ?total) ?randomOrder WHERE {
      ?randomOrder a schema:Order ; schema:orderedItem ?item .
    } GROUP BY ?randomOrder }}}
    FILTER(?min = ?total)
   } ORDER BY RAND() LIMIT 1}
    FILTER NOT EXISTS { mosp:Agent st:locatedAt ?anywhere }
}

prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
prefix mosp: <http://dfki.de/examples/stig_mosp#>
prefix ord: <http://dfki.de/examples/stig_mosp/orders#>
prefix st: <http://dfki.de/stigld/>
prefix schema: <http://schema.org/>

DELETE {
  ?lastElement rdf:rest rdf:nil .
  ?emptyList rdf:contents rdf:nil .
}
INSERT {

  ### Log produced product in order sequence
  ?sequenceElement rdf:first ?producedClass ;
                   rdf:rest rdf:nil .
  ?lastElement rdf:rest ?sequenceElement .
  ?emptyList rdf:contents ?sequenceElement .

  ### Add Item to the stack
  ?stack a mosp:Stack ; mosp:forOrder ?order ; schema:orderedItem ?item .
  ?item a ?producedClass .

  ## Add a pheromone marker to the order
  ?marker a st:Trace .
  ?order st:carries ?marker .
}  WHERE {

  OPTIONAL { ?emptyList rdf:contents rdf:nil . }
  OPTIONAL { ?lastElement rdf:rest rdf:nil . }

  BIND(BNODE() as ?sequenceElement)

  ## Agent picks an item out of the order list at random
  {SELECT ?producedClass ?at WHERE {
    mosp:Agent st:locatedAt ?at .
    ?at schema:orderedItem ?producedClass .
    FILTER NOT EXISTS { ?stack a mosp:Stack ; schema:orderedItem [ a ?producedClass ]. }
  } ORDER BY RAND() LIMIT 1}

  ## Find all orders that require the selected product
  {SELECT DISTINCT (BNODE() as ?marker) ?order ?producedClass WHERE {
    ?order schema:orderedItem ?producedClass .
  }}

  ## Add the newly produced product to a stack; create new stack where necessary
  {SELECT (BNODE() as ?item) (UUID() as ?stack) ?order ?producedClass WHERE {
    ?order schema:orderedItem ?producedClass .
    FILTER NOT EXISTS { ?stack a mosp:Stack ; mosp:forOrder ?order }
  }
  } UNION
  {SELECT (BNODE() as ?item) ?stack ?order WHERE {
    ?stack a mosp:Stack ; mosp:forOrder ?order .
  }}
}

PREFIX sc: <http://purl.org/science/owl/sciencecommons/>
prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
prefix mosp: <http://dfki.de/examples/stig_mosp#>
prefix ord: <http://dfki.de/examples/stig_mosp/orders#>
prefix st: <http://dfki.de/stigld/>
prefix schema: <http://schema.org/>


#### DELETE ADJACENT RELATIONS
DELETE {
  ?other st:adjacentTo ?finishedOrder .
  ?finishedOrder st:adjacentTo ?other .
}
WHERE {
  ?finishedOrder a st:Topos , schema:Order ; st:adjacentTo ?other .
  ?other a st:Topos, schema:Order ; st:adjacentTo ?finishedOrder .

  {SELECT ?finishedOrder ?finishedStack {
    ?finishedOrder a schema:Order ; schema:orderedItem ?item .
    ?finishedStack a mosp:Stack ; mosp:forOrder ?finishedOrder .

    FILTER NOT EXISTS {
      ?finishedOrder a schema:Order ; schema:orderedItem ?notFinished .
      FILTER NOT EXISTS {?finishedStack a mosp:Stack ; mosp:forOrder ?finishedOrder ; schema:orderedItem [ a ?notFinished ]}
    }
  }}
} ;

#### LOG CURRENT NUMBER OF OPEN stacks
DELETE {  mosp:maxStackCount rdf:value  ?oldCount . }
INSERT {  mosp:maxStackCount rdf:value  ?new . }
WHERE {
  {SELECT (count(?stack) as ?newCount) WHERE {
      ?stack a mosp:Stack .
  }}
  OPTIONAL {mosp:maxStackCount rdf:value ?oldCount .}
  BIND(IF(bound(?oldCount), ?oldCount, 0) as ?old)
  BIND(IF(?newCount > ?old, ?newCount,?old) as ?new)
} ;

#### DELETE ORDERS AND THEIR STACKS WHEN FINISHED
DELETE {
  ?finishedStack a mosp:Stack; mosp:forOrder ?finishedOrder ; schema:orderedItem ?item .
  ?item ?p ?o .
  ?finishedOrder a schema:Order , st:Topos ; st:adjacentTo ?other ; st:carries ?trace ; schema:orderedItem ?itemClass.
  ?trace a st:Trace ; ?t_p ?t_o .
}
WHERE {
  ?finishedOrder a schema:Order , st:Topos ; st:carries ?trace ; schema:orderedItem ?itemClass .
  ?trace a st:Trace ; ?t_p ?t_o .
  FILTER(isblank(?trace))

  ?finishedStack mosp:forOrder ?finishedOrder ; schema:orderedItem ?item .
  ?item ?p ?o .
  FILTER(isblank(?item))

  {SELECT ?finishedOrder ?finishedStack {
      ?finishedOrder a schema:Order ; schema:orderedItem ?item .
      ?finishedStack a mosp:Stack ; mosp:forOrder ?finishedOrder .

      FILTER NOT EXISTS {
        ?finishedOrder a schema:Order ; schema:orderedItem ?notFinished .
        FILTER NOT EXISTS {?finishedStack a mosp:Stack ; mosp:forOrder ?finishedOrder ; schema:orderedItem [ a ?notFinished ]}
      }
    }}
} ;


#### AGENT IS NOW MOST LIKELY IN NIRVANA. REMOVE HIS LOCALITY TO PICK A NEW
#### RANDOM ORDER NEXT TIME

DELETE {
  mosp:Agent st:locatedAt ?at
} WHERE {
  mosp:Agent st:locatedAt ?at .
  FILTER NOT EXISTS {
    ?at a st:Topos .
  }
}

